import React, { useEffect, useRef, useState } from "react";
import Router from "next/router";
import { withApollo } from "../../utils/withApollo";
import { Formik, Form } from "formik";
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl";
import { Box, Button, Input, Textarea } from "@chakra-ui/core";
import NavBar from "../../components/NavBar";
import ReactMarkdown from "react-markdown";

const edit: React.FC<{}> = () => {
  const router = Router;
  const handleSubmit = () => {};
  const { data } = useGetPostFromUrl();
  const [title, setTitle] = useState("");

  useEffect(() => {
    if (data) setTitle(data.post.title);
  }, [data?.post.title]);
  return (
    <>
      <NavBar />
      <Box display="flex" mt="10vh">
        {!data ? (
          ""
        ) : (
          <Box w="50%">
            <Formik
              initialValues={{
                title: title,
              }}
              onSubmit={handleSubmit}
            >
              <Form>
                <Input
                  textAlign="center"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                />
                <Button ml="auto">submit</Button>
                <Textarea>qrqwr</Textarea>
                <ReactMarkdown></ReactMarkdown>
              </Form>
            </Formik>
          </Box>
        )}
      </Box>
    </>
  );
};

export default withApollo({ ssr: false })(edit);
