import { Box, Button, ThemeProvider } from "@chakra-ui/core"
import Head from "next/head"
import React, { useEffect, useRef, useState } from "react"
import Footer from "../components/Footer"
import NavBar from "../components/NavBar"
import "../styles/globals.css"
import customTheme from "../utils/theme"
import { withApollo } from "../utils/withApollo"

function MyApp({ Component, pageProps }) {
  const [dark, setDark] = useState("light")
  const color = useRef(null)
  const moonSun = useRef(null)
  useEffect(() => {
    if (localStorage.getItem("sid") === null) {
      localStorage.setItem("sid", "light")
    } else if (localStorage.getItem("sid") === "dark") {
      localStorage.setItem("sid", "dark")
      setDark('dark')
    }
  }, [])
  // color.current.style.backgroundColor = "#000"
  // color.current.style.color = "#aaa"

  const handleColor = () => {
    const localItem = localStorage.getItem("sid")
    if (localItem === "light") {
      color.current.style.backgroundColor = "#000"
      color.current.style.color = "#eee"
      localStorage.setItem("sid", "dark")
    } else {
      color.current.style.backgroundColor = "#fff"
      color.current.style.color = "#000"
      localStorage.setItem("sid", "light")
    }
  }
  return (
    <ThemeProvider theme={customTheme}>
      <Box
        ref={moonSun}
        backgroundColor="black"
        color="white"
        onClick={handleColor}
        position="fixed"
        right="55px"
        top="80%"
        fontSize="20px"
        outline="none"
        w="1px !important"
        h="1px"
        cursor="pointer"
      >
        {localStorage.getItem("sid") === null ? 'ðŸŒž' : localStorage.getItem("sid") === 'light' ? 'ðŸŒž' : 'ðŸŒ•'}

      </Box>
      <Head>
        <title>wooo's blog</title>
      </Head>
      <Box
        ref={color}
        transition=".4s"
        minHeight="100vh"
        wordBreak="break-all"
        overflowX="hidden"
      >
        <NavBar />
        <Component {...pageProps} />
        <Footer />
      </Box>
    </ThemeProvider>
  )
}

export default MyApp
