import React, { useEffect, useRef, useState } from "react";
import Router from "next/router";
import { withApollo } from "../../utils/withApollo";
import { Formik, Form } from "formik";
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl";
import { Box, Button, Input, Textarea } from "@chakra-ui/core";
import NavBar from "../../components/NavBar";
import ReactMarkdown from "react-markdown";
// import SyntaxHighlighter from "react-syntax-highlighter";
// import { dark } from "react-syntax-highlighter/dist/esm/styles/prism";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";

const edit: React.FC<{}> = () => {
  const router = Router;
  const handleSubmit = () => {};
  const { data } = useGetPostFromUrl();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (data) {
      setTitle(data.post.title);
      setDescription(data.post.description);
    }
  }, [data]);
  return (
    <>
      <NavBar />
      <Box display="flex" justifyContent="center" mt="10vh">
        {!data ? (
          ""
        ) : (
          <Box w="55%">
            <Formik
              initialValues={{
                title: title,
              }}
              onSubmit={handleSubmit}
            >
              <Form>
                <Input
                  textAlign="center"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                />
                <Textarea
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                ></Textarea>
                <Button ml="auto">submit</Button>
              </Form>
            </Formik>
            <ReactMarkdown
              source={description}
              renderers={{
                code: Component,
              }}
            ></ReactMarkdown>
          </Box>
        )}
      </Box>
    </>
  );
};

const Component = ({ value, language }) => {
  return (
    <SyntaxHighlighter language={language ?? null} style={dark}>
      {value ?? ""}
    </SyntaxHighlighter>
  );
};

export default withApollo({ ssr: false })(edit);
