import React from "react";
import { usePostsQuery } from "../generated/graphql";
import NextLink from "next/link";
import ReactMarkdown from "react-markdown";
import NavBar from "../components/NavBar";
import { withApollo } from "../utils/withApollo";
import { Box, Text, Image } from "@chakra-ui/core";

interface homeProps {}

export interface RequestInfo {
  data: string;
}

const Home: React.FC<{}> = () => {
  const { data, loading } = usePostsQuery();

  if (!loading && !data) {
    return <div>sorry, somthing happend</div>;
  }

  return data ? (
    <>
      <NavBar />
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
      >
        <Image src="/main.jpg" w="75%" h="75%" mt="50px" mb="50px" />
        <Box w="75%" style={{ columnCount: 3, columnGap: "20px" }}>
          {data.posts.map((post) => (
            <Box
              borderRadius="5px"
              border="1px solid black"
              boxShadow="2px 2px 5px rgba(0,0,0,0.5)"
              p="20px"
              mb="40px"
              display="flex"
              justifyContent="center"
            >
              <NextLink href="/:id" as={`/post/${post.id}`}>
                <Text>{post.title}</Text>
              </NextLink>
              <Box position="sticky" top="10px">
                {post.createdAt.replace(/-/gi, ".")}
              </Box>
            </Box>
          ))}
        </Box>
      </Box>
    </>
  ) : (
    <div>""</div>
  );
};

export default withApollo({ ssr: true })(Home);
