import { Box, Text } from "@chakra-ui/core"
import React, { useEffect } from "react"
import NavBar from "../components/NavBar"
import { useTagsQuery } from "../generated/graphql"
import { withApollo } from "../utils/withApollo"

interface tagsProps { }

const tags: React.FC<{}> = () => {
  const { data, loading } = useTagsQuery()
  if (!data && !loading) return <div>server error</div>

  let tags = data?.tags
  // useEffect(() => {
  // tags =
  // }, [data?.tags])

  console.log(tags)
  return (
    <>
      {!data?.tags ? (
        <div />
      ) : (
          <Box fontSize="50px">
            {data!.tags.map((t) => {
              <>
                <Box position="absolute" top="45%" left="45%" w="50px" h="50px" border="1px solid red">
                  <Text fontSize="100px" color="red">t.text</Text>
                </Box>
              </>
            })}
          </Box>
        )}
    </>
  )
}
export default withApollo({ ssr: true })(tags)
