import React, { useEffect, useRef } from "react";
import Router from "next/router";
import { withApollo } from "../../utils/withApollo";
import { Formik, Form } from "formik";
import { usePostQuery, useUpdatePostMutation } from "../../generated/graphql";
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl";
const edit: React.FC<{}> = () => {
  const router = Router;
  const { data, loading } = useGetPostFromUrl();

  if (!loading) {
    const post = usePostQuery({
      variables: { id: data?.post?.id },
    });
    const idEl = useRef(null);
    const passwordEl = useRef(null);
    const title = data.post.title;
    const description = data.post.description;
    let handleSubmit = async () => {
      // e.preventDefault();
      const id = idEl.current.value;
      const password = passwordEl.current.value;
    };
  }
  return (
    <>
      <Formik
        initialValues={{
          id: "",
          password: "",
        }}
        onSubmit={handleSubmit}
      >
        <Form>
          <input name="id" ref={idEl} type="text" />
          <input name="password" ref={passwordEl} type="password" />
          <button type="submit">submit</button>
        </Form>
      </Formik>
    </>
  );
};

export default withApollo({ ssr: false })(edit);
