import React from "react";
import styled from "styled-components";
import NextLink from "next/link";

const Container = styled.div`
  width: 100vw;
  height: 10vh;
  display: flex;
  justify-content: center;
  background-color: #333;
  font-family: cursive;
`;

const Bar = styled.div`
  width: 65%;
  /* position:fixed; */
  top: 0px;
  background-color: #333333;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  border-bottom: 1px solid #333333;
  z-index: 1;
`;

const About = styled.div`
  width: 10vw;
  min-width: 150px;
  height: 3vh;
  text-align: center;
  align-self: center;
  /* background-color: grey; */
  @media (max-width: 780px) {
    display: none;
  }
`;
const SearchBar = styled.div`
  width: 7vw;
  min-width: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  /* background-color: grey; */
  @media (max-width: 780px) {
    display: none;
  }
`;
const Categories = styled.div`
  width: 150px;
  min-width: 150px;
  height: 3vh;
  text-align: center;
  align-self: center;
  /* float:left; */
  /* background-color: grey; */
  @media (max-width: 780px) {
    display: none;
  }
`;
const Logo = styled.div`
  width: 10vw;
  height: 3vh;
  position: absolute;
  left: 15vw;
  top: 3.5vh;
  text-align: center;
  align-self: center;
  @media (max-width: 780px) {
    left: 45vw;
  }
  /* background-color: red; */
`;

const Nav = styled.div`
  width: 400px;
  min-width: 400px;
  max-width: 400px;
  display: flex;
  justify-content: center;
`;

interface NavBarProps {}

const NavBar: React.FC<{}> = () => {
  return (
    <Container>
      <Bar>
        {/* <InnerBar> */}
        <NextLink href="/">
          <Logo style={{ cursor: "pointer", color: "#fff" }}>LOGO</Logo>
        </NextLink>
        <Nav>
          <Categories style={{ color: "#fff" }}>
            <NextLink href="/catagories">CATAGORIES</NextLink>
          </Categories>
          <About style={{ color: "#fff" }}>
            <NextLink href="/about">ABOUT</NextLink>
          </About>
          <SearchBar>
            <input
              placeholder="🔍"
              style={{
                width: "7vw",
                textIndent: "5px",
                height: "30px",
                fontSize: "15px",
              }}
            ></input>
          </SearchBar>
        </Nav>

        {/* </InnerBar> */}
      </Bar>
    </Container>
  );
};

export default NavBar;
