import React from "react";
import { useMeQuery, usePostQuery } from "../../generated/graphql";
import ReactMarkdown from "react-markdown";
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl";
import { withApollo } from "../../utils/withApollo";
import Rotuer from "next/router";
import { Box } from "@chakra-ui/core";
import CKEditor from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";

interface postPagesProps<> {}

const PostPage: React.FC<postPagesProps> = (props) => {
  const me = useMeQuery();
  const { data, loading } = useGetPostFromUrl();
  const ME = me?.data?.me;
  const router = Rotuer;

  const getBackToHome = () => {
    router.push("/");
  };
  return (
    <>
      {!ME ? (
        ""
      ) : (
        <>
          <Box />
          <Box>
            <Box>
              <Box>{data?.post?.id}</Box>
              <Box>{data?.post?.title}</Box>
              <Box>
                <ReactMarkdown
                  escapeHtml={false}
                  source={data?.post?.description}
                ></ReactMarkdown>
              </Box>
            </Box>
          </Box>
          <CKEditor
            editor={ClassicEditor}
            data="<p>Hello from CKEditor 5!</p>"
            onInit={(editor) => {
              // You can store the "editor" and use when it is needed.
              console.log("Editor is ready to use!", editor);
            }}
            onChange={(event, editor) => {
              const data = editor.getData();
              console.log({ event, editor, data });
            }}
            onBlur={(event, editor) => {
              console.log("Blur.", editor);
            }}
            onFocus={(event, editor) => {
              console.log("Focus.", editor);
            }}
          />
        </>
      )}
    </>
  );
};

export default withApollo({ ssr: true })(PostPage);
