import { Box } from "@chakra-ui/core";
import React, { InputHTMLAttributes, Props } from "react";
import NextLink from "next/link";
import { useGetPostFromUrl } from "../utils/useGetPostFromUrl";

type EditDeleteButtonsProps = InputHTMLAttributes<HTMLInputElemnt> & {
  postId: number;
};

const EditDeleteButtons: React.FC<{ EditDeleteButtonsProps }> = ({
  postId,
  ...props
}) => {
  return (
    <>
      <Box
        position="absolute"
        size="20px"
        top="0px"
        right="0px"
        backgroundColor="#ff1744"
        borderRadius="50%"
        cursor="pointer"
      />
      <NextLink href="/:id" as={`/${postId}`}>
        <Box
          position="absolute"
          top="0px"
          right="20px"
          size="20px"
          backgroundColor="#3f51b5"
          borderRadius="50%"
          cursor="pointer"
        />
      </NextLink>
    </>
  );
};

export default EditDeleteButtons;
