import React, { Component, useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import ReactMarkdown from "react-markdown";
import Img1 from "../materials/mainImg.jpg";
import dotenv, { load } from "dotenv";
import { useQuery, gql } from "@apollo/client";
import { usePostsQuery } from "../generated/graphql";
dotenv.config();

const Container = styled.div`
  margin-top: 10vh;
  width: 100vw;
  height: 100%;
  display: flex;
  justify-content: center;
`;
const InnerContainer = styled.div`
  width: 75vw;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;
const MainImage = styled.img`
  margin-top: 5vh;
  width: 70vw;
  height: 100%;
  @media (max-width: 780px) {
    width: 90vw;
  }
`;
const BottomContainer = styled.div`
  margin-top: 100px;
  width: 70vw;
  height: 1000px;
  @media (max-width: 780px) {
    width: 90vw;
  }
`;
const Card = styled.div`
  margin: 10px 0 10px 0;
  width: 100%;
  height: auto;
  min-height: 200px;
  border: 1px solid black;
  display: flex;
  flex-direction: column;
  align-items: center;
  align-content: center;
  justify-items: center;
  @media (max-width: 780px) {
    height: auto;
  }
`;
const StyledLink = styled(Link)`
  text-decoration: none;
  &:focus,
  &:hover,
  &:visited,
  &:link,
  &:active {
    text-decoration: none;
  }
`;
const Title = styled.div`
  font-size: 50px;
  margin: 20px 0 20px 0;
  color: dimgrey;
  font-weight: 100;
  text-overflow: hidden;
  font-family: "Do Hyeon", sans-serif;
  @media (max-width: 780px) {
    font-size: 25px;
  }
`;
const Content = styled.div`
  font-size: 20px;
  text-align: left;
  text-decoration: none;
  text-indent: 20px;
  width: 65vw;
  font-weight: 500;
  overflow: hidden;
  line-height: 25px;
  @media (max-width: 780px) {
    font-size: 15px;
    line-height: 18px;
    width: 90%;
  }
`;
const Date = styled.div`
  position: absolute;
  right: 15vw;
  @media (max-width: 780px) {
    right: 5vw;
  }
`;
const Tag = styled.div``;
const Loading = styled.div`
  width: 500px;
  height: 500px;
  position: absolute;
  top: 45%;
  font-size: 50px;
`;

interface homeProps {}

export interface RequestInfo {
  data: string;
}

const Home: React.FC<{}> = () => {
  const [post, setPost] = useState([]);
  const { data, loading } = usePostsQuery();
  // useEffect(() => {
  //   setPost([]);
  // }, []);

  if (!loading && !data) {
    return <div>sorry, somthing happend</div>;
  }

  // const posts = post;
  // const postsList = posts.map((post) => (
  //   <Card key={post.id} id={post.id}>
  //     {console.log(post)}
  //     <StyledLink to={`/posts/${post._id}`}>
  //       <Title>{post.title}</Title>
  //     </StyledLink>
  //     <Content>
  //       {post.description ? (
  //         <Content>
  //           <ReactMarkdown
  //             escapeHtml={false}
  //             source={
  //               post.description.replace(/(\r\n\t|\n|\r\t)/gm, "") + "..."
  //             }
  //           ></ReactMarkdown>
  //         </Content>
  //       ) : (
  //         "error :("
  //       )}
  //     </Content>
  //     <Date>{post.date ? post.date.substring(0, 10) : "error :("}</Date>
  //     <Tag>{post.tag}</Tag>
  //   </Card>
  // ));

  return data ? (
    <div>
      <Container>
        <InnerContainer>
          {data.posts.map((post) => (
            <Card>
              {console.log(post)}
              <StyledLink to={`/posts/${post.id}`}>
                <Title>{post.title}</Title>
              </StyledLink>
              <Content>
                {post.description ? (
                  <Content>
                    <ReactMarkdown
                      escapeHtml={false}
                      source={
                        post.description.replace(/(\r\n\t|\n|\r\t)/gm, "") +
                        "..."
                      }
                    ></ReactMarkdown>
                  </Content>
                ) : (
                  "error :("
                )}
              </Content>
              <Date>{post.createdAt ? post.createdAt : "error :("}</Date>
            </Card>
          ))}
        </InnerContainer>
      </Container>
    </div>
  ) : (
    <div>"hello"</div>
  );
};

export default Home;
