import React, { useEffect, useRef } from "react";
import {
  LoginDocument,
  LoginMutation,
  MeDocument,
  MeQuery,
  useLoginMutation,
} from "../generated/graphql";
import Router from "next/router";
import { withApollo } from "../utils/withApollo";
import { Formik, Form } from "formik";

const admin: React.FC<{}> = () => {
  const router = Router;
  const [login] = useLoginMutation();
  const idEl = useRef(null);
  const passwordEl = useRef(null);

  let handleSubmit = () => {
    // e.preventDefault();
    const id = idEl.current.value;
    const password = passwordEl.current.value;
    console.log(
      login({
        variables: { id: id, password: password },
      })
    );
  };
  return (
    <>
      <Formik
        initialValues={{
          id: "",
          password: "",
        }}
        onSubmit={async (values, { setErrors }) => {
          console.log(values.id);
          const response = await login({
            variables: values,
            update: (cache, { data }) => {
              console.log(cache);
              cache.writeQuery<MeQuery>({
                query: MeDocument,
                data: {
                  __typename: "Query",
                  me: data?.login,
                },
              });
              // cache.evict({ fieldName: "posts:{}" });
            },
          });
        }}
      >
        <Form>
          <input name="id" ref={idEl} type="text" />
          <input name="password" ref={passwordEl} type="password" />
          <button type="submit" onSubmit={handleSubmit}>
            submit
          </button>
        </Form>
      </Formik>
    </>
  );
};

export default withApollo({ ssr: true })(admin);
