import React from "react";
import { useMeQuery, usePostsQuery } from "../generated/graphql";
import NextLink from "next/link";
import NavBar from "../components/NavBar";
import { withApollo } from "../utils/withApollo";
import { Box, Text, Image, Link, Icon } from "@chakra-ui/core";

interface homeProps {}

export interface RequestInfo {
  data: string;
}

const Home: React.FC<{}> = () => {
  const { data, loading } = usePostsQuery();
  const me = useMeQuery();

  if (!loading && !data) {
    return <div>sorry, somthing happend</div>;
  }
  const ME = me.data?.me;
  return data ? (
    <>
      <NavBar />
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
      >
        <Image src="/main.jpg" w="75%" h="75%" mt="50px" mb="50px" />
        <Box
          w="75%"
          style={{
            display: "flex",
            alignItems: "left",
            justifyContent: "left",
            flexDirection: "row",
            flexWrap: "wrap",
            flexFlow: "row wrap",
            alignContent: "flex-end",
          }}
        >
          {data.posts.map((post) => (
            <Box
              borderRadius="5px"
              border="1px solid black"
              boxShadow="2px 2px 5px rgba(0,0,0,0.5)"
              p="20px"
              w="30%"
              ml="3%"
              mb="40px"
              display="flex"
              justifyContent="center"
              position="relative"
            >
              <NextLink href="/:id" as={`/post/${post.id}`}>
                <Link _hover={{ textDecoration: "none" }}>
                  <Text>{post.title}</Text>
                </Link>
              </NextLink>
              <Box position="absolute" ml="9%">
                {/* {post.createdAt} */}
              </Box>
              {ME ? (
                <Box w="100%">
                  <Box
                    position="absolute"
                    size="20px"
                    top="0px"
                    right="0px"
                    backgroundColor="#ff1744"
                    borderRadius="50%"
                  />
                  <Box
                    position="absolute"
                    top="0px"
                    right="20px"
                    size="20px"
                    backgroundColor="#18ffff"
                    borderRadius="50%"
                  />
                </Box>
              ) : (
                ""
              )}
            </Box>
          ))}
        </Box>
      </Box>
    </>
  ) : (
    <div>""</div>
  );
};

export default withApollo({ ssr: true })(Home);
