import { Box } from "@chakra-ui/core"
import React from "react"
import ReactMarkdown from "react-markdown"
import SyntaxHighlighter from "react-syntax-highlighter"
import dark from "react-syntax-highlighter/dist/cjs/styles/hljs/a11y-dark"
import EditDeleteButtons from "../../components/EditDeleteButtons"
import NavBar from "../../components/NavBar"
import { useMeQuery } from "../../generated/graphql"
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl"
import { withApollo } from "../../utils/withApollo"
interface postPagesProps<> { }

const PostPage: React.FC<postPagesProps> = (props) => {
  const { data, loading } = useGetPostFromUrl()
  const me = useMeQuery()
  const Me = me.data?.me
  console.log(data)
  return (
    <>
      {Me ? (
        <Box position="fixed" right="15px" top="45vh">
          <EditDeleteButtons postId={parseInt(data?.post?.id)} />{" "}
        </Box>
      ) : (
          ""
        )}
      <Box display="flex" justifyContent="center" mt="20vh">
        <Box
          className="edit-container"
          minWidth="700px"
          w="55%"
          display="flex"
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
        >
          <Box
            w="80%"
            borderBottom="1px solid #666"
            p="50px"
            textAlign="center"
            fontSize="42px"
          >
            {data?.post?.title}
          </Box>
          <Box p="20px" mt="5%" textAlign="left" w="80%" lineHeight="80%">
            <ReactMarkdown
              escapeHtml={false}
              source={data?.post?.description}
              renderers={{
                code: handleCode,
                image: ({
                  alt,
                  src,
                  title,
                }: {
                  alt?: string
                  src?: string
                  title?: string
                }) => (
                    <img
                      alt={alt}
                      src={src}
                      title={title}
                      style={{
                        maxWidth: "700px",
                        margin: "0 auto",
                        display: "table",
                        padding: "40px",
                      }}
                    />
                  ),
              }}
            ></ReactMarkdown>
          </Box>
        </Box>
      </Box>
    </>
  )
}

const handleCode = ({ value, language }) => {
  return (
    <Box lineHeight={"20px"} fontWeight={"500"} fontSize="12px">
      <SyntaxHighlighter
        showLineNumbers={true}
        startingLineNumber={1}
        language={language ?? null}
        style={dark}
        lineNumberStyle={{ color: "#ddd", fontSize: 12 }}
        wrapLines={true}
      >
        {value ?? null}
      </SyntaxHighlighter>
    </Box>
  )
}

export default withApollo({ ssr: true })(PostPage)
