import React from "react";
import NextLink from "next/link";
import { Box, Flex } from "@chakra-ui/core";
import { useLogoutMutation, useMeQuery } from "../generated/graphql";
import Router from "next/router";

interface NavBarProps {}

const NavBar: React.FC<{}> = () => {
  const { data, loading } = useMeQuery();
  const [logout] = useLogoutMutation();
  const router = Router;

  const handleClick = async () => {
    const data = await logout();
    console.log(data);
    router.reload();
  };
  return (
    <Box
      p={"absolute"}
      w={"100vw"}
      h={"80px"}
      backgroundColor={"#333"}
      display={"flex"}
    >
      {/* <InnerBar> */}
      <Box style={{ cursor: "pointer", color: "#fff" }} m="auto">
        <NextLink href="/">LOGO</NextLink>
      </Box>
      <Box display="flex" justifyContent="space-evenly" margin="auto">
        <Box style={{ color: "#fff" }} m="auto" mr="10%">
          <NextLink href="/tags">TAGS</NextLink>
        </Box>
        <Box style={{ color: "#fff" }} m="auto" mr="10%">
          <NextLink href="/about">ABOUT</NextLink>
        </Box>
        <Box m="auto">
          <input
            placeholder="🔍"
            style={{
              outline: "none",
              width: "7vw",
              textIndent: "5px",
              height: "30px",
              fontSize: "15px",
            }}
          ></input>
        </Box>
      </Box>
      {data?.me ? (
        <Box
          p="absolute"
          right="15px"
          mt="25px"
          w="150px"
          h="50px"
          color="red"
          textAlign="center"
          onClick={handleClick}
        >
          LOGOUT
        </Box>
      ) : (
        ""
      )}
    </Box>
  );
};

export default NavBar;
