import React from "react"
import { useMeQuery, usePostsQuery, useTagQuery, useTagsQuery } from "../../generated/graphql"
import NextLink from "next/link"
import { withApollo } from "../../utils/withApollo"
import { Box, Text, Image, Link } from "@chakra-ui/core"
import EditDeleteButtons from "../../components/EditDeleteButtons"
import { useGetIntId } from '../../utils/useGetIntId'

interface homeProps { }

export interface RequestInfo {
    data: string
}

const Home: React.FC<{}> = () => {
    const id = useGetIntId()
    const { data, loading } = useTagQuery({
        variables: { id: id }
    })
    const me = useMeQuery()

    // if (!loading && !data) {
    //     return <div>sorry, somthing happend</div>
    // }
    setInterval(() => {

        console.log(data)
    }, 1000)
    const ME = me.data?.me
    return data ? (
        <>
            <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                flexDirection="column"
            >
                <Image src="/main.jpg" w="75%" h="75%" mt="50px" mb="50px" />
                <Box
                    w="75%"
                    style={{
                        display: "flex",
                        alignItems: "left",
                        justifyContent: "left",
                        flexDirection: "row",
                        flexWrap: "wrap",
                        flexFlow: "row wrap",
                        alignContent: "flex-end",
                    }}
                >
                    {data?.tag?.posts?.map((t) => (
                        <Box
                            borderRadius="5px"
                            border="1px solid grey"
                            shadow=" 1px 1px 1px grey"
                            boxShadow="2px 2px 5px rgba(0,0,0,0.5)"
                            p="20px"
                            w="30%"
                            ml="3%"
                            mb="40px"
                            display="flex"
                            justifyContent="center"
                            position="relative"
                            textAlign="center"
                        >
                            <NextLink href="/:id" as={`/post/${t.id}`}>
                                <Link _hover={{ textDecoration: "none" }}>
                                    <Text>{t.title}</Text>
                                </Link>
                            </NextLink>
                            {/* <Box position="absolute" ml="9%">
                {post.createdAt}
              </Box> */}
                            {ME ? (
                                <EditDeleteButtons title={t.title} postId={parseInt(t.id)} />
                            ) : (
                                    ""
                                )}
                        </Box>
                    ))}
                </Box>
            </Box>
        </>
    ) : (
            <div></div>
        )
}

export default withApollo({ ssr: true })(Home)
