import { Box, Text } from "@chakra-ui/core"
import React, { useEffect } from "react"
import NavBar from "../components/NavBar"
import { useAddTagPostMutation, usePostsLazyQuery, usePostsQuery, useTagsQuery } from "../generated/graphql"
import { withApollo } from "../utils/withApollo"
import { Form, Formik } from "formik"



interface relProps { }

const rel: React.FC<{}> = () => {
    const { data, loading } = useTagsQuery()
    const posts = usePostsQuery()
    const rel = useAddTagPostMutation()
    if (!data && !loading) return <div>server error</div>

    const router = Router
    const [login] = useLoginMutation()
    const idEl = useRef(null)
    const passwordEl = useRef(null)

    let handleSubmit = async () => {
        // e.preventDefault();
        const id = idEl.current.value
        const password = passwordEl.current.value
        const data = await login({
            variables: { id: id, password: password },
        })
        if (data.data.login) router.push("/")
        else alert("authentication failed")
    }
    return (
        <>
            <Box w="100vw" h="100vh" display="flex" justifyContent="center" alignItems='center'>
                <Formik
                    initialValues={{
                        id: "",
                        password: "",
                    }}
                    onSubmit={handleSubmit}
                >
                    <Form>
                        <input name="id" ref={idEl} type="text" />
                        <input name="password" ref={passwordEl} type="password" />
                        <button type="submit">submit</button>
                    </Form>
                </Formik>
                <Box w="400px" h="400px" border="1px solid black" display="flex" justifyContent="center" alignItems="center">
                    <Box>
                        {posts?.data?.posts?.map((t) => (
                            <Text>{t.title}({t.id})</Text>
                        ))}
                    </Box>
                </Box>
                <Box w="400px" h="400px" border="1px solid black" display="flex" justifyContent="center" alignItems="center">
                    <Box>
                        {data?.tags?.map((t) => (
                            <Text>{t.text}({t.postsLength})</Text>
                        ))}
                    </Box>
                </Box>
            </Box>

        </>
    )
}
export default withApollo({ ssr: true })(rel)
