import React from "react";
import styled from "styled-components";
import { usePostQuery } from "../../generated/graphql";
import NextLink from "next/link";
import ReactMarkdown from "react-markdown";
import Link from "next/link";

const Title = styled.div`
  font-size: 50px;
  font-family: "Do Hyeon", sans-serif;
  margin: 20px 0 100px 0;
  text-align: center;
  @media (max-width: 780px) {
    /* width:90vw; */
  }
`;
const Content = styled.div`
  font-size: 120%;
`;
const Date = styled.div`
  text-align: right;
`;
const Container = styled.div`
  margin-top: 10vh;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  text-align: left;
  word-break: break-all;
`;
const InnerContainer = styled.div`
  width: 55%;
  min-width: 700px;
  margin-top: 10%;
  border-bottom: 1px solid black;
  border-top: 1px solid black;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-bottom: 500px;
  @media (max-width: 780px) {
    width: 90vw;
    min-width: 90vw;
  }
`;

interface MatchParams {
  _id: string;
}

export interface RouteComponentProps<MatchParams> {
  match: any;
}

interface postPagesProps extends RouteComponentProps<MatchParams> {}

const PostPage: React.FC<postPagesProps> = (props) => {
  const ID = parseInt(props.match.params._id);
  const { data, loading } = usePostQuery({
    variables: {
      id: ID,
    },
  });

  console.log(data);
  return (
    <>
      <Container>
        <InnerContainer>
          <Date>{data?.post?.id}</Date>
          <Title>{data?.post?.title}</Title>
          <Content>
            <ReactMarkdown
              escapeHtml={false}
              source={data?.post?.description}
            ></ReactMarkdown>
          </Content>
        </InnerContainer>
      </Container>
    </>
  );
};

export default PostPage;
