import { Box, Button, Input, Textarea } from "@chakra-ui/core"
import { Form, Formik } from "formik"
import Router from "next/router"
import React, { useRef, useState } from "react"
import ReactMarkdown from "react-markdown"
import SyntaxHighlighter from "react-syntax-highlighter"
import { dark } from "react-syntax-highlighter/dist/cjs/styles/hljs"
import { useCreatePostMutation } from "../generated/graphql"
import { withApollo } from "../utils/withApollo"

const edit: React.FC<{}> = () => {
  const router = Router
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [mdState, setMdState] = useState(1)
  const [createPost] = useCreatePostMutation()
  const handleSubmit = async () => {
    console.log("handlesubmit")
    await createPost({
      variables: {
        input: { title: title, description: description },
      },
    })
    router.back()
  }

  const handleMd = () => {
    if (mdState === 1) {
      md.current.style.display = "none"
      setMdState(0)
    } else {
      md.current.style.display = "inline"
      setMdState(1)
    }
  }
  const md = useRef(null)

  return (
    <>
      <Box display="flex" justifyContent="center" mt="10vh">
        <Box w="55%">
          <Formik
            initialValues={{
              title: title,
            }}
            onSubmit={handleSubmit}
          >
            <Form>
              <Input
                textAlign="center"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <Textarea
                h="100vh"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></Textarea>
              <Button type="submit" ml="auto">
                submit
              </Button>
            </Form>
          </Formik>
          <Button onClick={handleMd}>펼치기</Button>
          <Box ref={md}>
            <ReactMarkdown
              source={description}
              renderers={{
                code: Component,
              }}
            ></ReactMarkdown>
          </Box>
        </Box>
      </Box>
    </>
  )
}

const Component = ({ value, language }) => {
  return (
    <SyntaxHighlighter language={language ?? null} style={dark}>
      {value ?? ""}
    </SyntaxHighlighter>
  )
}

export default withApollo({ ssr: false })(edit)
