import React, { useEffect, useRef } from "react";
import {
  LoginDocument,
  LoginMutation,
  MeDocument,
  MeQuery,
  useLoginMutation,
} from "../generated/graphql";
import Router from "next/router";
import { withApollo } from "../utils/withApollo";
import { Formik, Form } from "formik";
import gql from "gql-tag";

const admin: React.FC<{}> = () => {
  const router = Router;
  const [login] = useLoginMutation();
  const idEl = useRef(null);
  const passwordEl = useRef(null);

  let handleSubmit = () => {
    // e.preventDefault();
    const id = idEl.current.value;
    const password = passwordEl.current.value;

    const requestBody = {
      query: `
        mutation{
          login(id:"${id}", password:"${password}")
        }
      `,
    };

    fetch("http://localhost:4000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("연결실패");
        }
        return res.json();
      })
      .then((resData) => {
        if (resData.data.login) router.back();
        else alert("authentication failed");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <Formik
        initialValues={{
          id: "",
          password: "",
        }}
        onSubmit={handleSubmit}
      >
        <Form>
          <input name="id" ref={idEl} type="text" />
          <input name="password" ref={passwordEl} type="password" />
          <button type="submit">submit</button>
        </Form>
      </Formik>
    </>
  );
};

export default withApollo({ ssr: true })(admin);
