import { Box } from "@chakra-ui/core"
import React from "react"
import ReactMarkdown from "react-markdown"
import EditDeleteButtons from "../../components/EditDeleteButtons"
import { useMeQuery } from "../../generated/graphql"
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl"
import { withApollo } from "../../utils/withApollo"
import { handleImage, handleCode, handleLink } from '../../utils/markdownRenderers'
interface postPagesProps<> { }

const PostPage: React.FC<postPagesProps> = (props) => {
  const { data, loading } = useGetPostFromUrl()
  const me = useMeQuery()
  const Me = me.data?.me
  console.log(data)
  return (
    <>
      <style jsx global>{`

        @media screen and (max-width: 768px) { 
          .postId-logo{
          }
          .postId-tags{
            display:none !important;
          }
          .postId-about{
            display:none;
          }
          .postId-searchbar{
            display:none;
          }
        }
      `}</style>
      {Me ? (
        <Box position="fixed" right="15px" top="45vh">
          <EditDeleteButtons postId={parseInt(data?.post?.id)} />{" "}
        </Box>
      ) : (
          <div />
        )}
      <Box display="flex" justifyContent="center" mt="20vh">
        <Box
          className="edit-container"
          minWidth="90vw"
          w="55%"
          display="flex"
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
          p="25px"
        >
          <Box
            w="80%"
            borderBottom="1px solid #666"
            p="50px"
            textAlign="center"
            fontSize="42px"
          >
            {data?.post?.title}
          </Box>
          <Box w="100%" textAlign="end" mt="50px">
            {data?.post.createdAt.slice(0, 10)}
          </Box>
          <Box p="20px" mt="5%" textAlign="left" w="800px" lineHeight="150%" >
            <ReactMarkdown
              escapeHtml={false}
              source={data?.post?.description}
              renderers={{
                code: handleCode,
                image: handleImage,
                link: handleLink

              }}
            ></ReactMarkdown>
          </Box>
        </Box>
      </Box>

      <style jsx>{`
          .edit-container {
            background-color: black;
          }
        `}</style>
    </>
  )
}

export default withApollo({ ssr: true })(PostPage)
