import React from "react";
import { useMeQuery, usePostQuery } from "../../generated/graphql";
import NextLink from "next/link";
import ReactMarkdown from "react-markdown";
import Link from "next/link";
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl";
import { withApollo } from "../../utils/withApollo";
import Rotuer from "next/router";
import { Box } from "@chakra-ui/core";

interface postPagesProps<> {}

const PostPage: React.FC<postPagesProps> = (props) => {
  const me = useMeQuery();
  const { data, loading } = useGetPostFromUrl();
  const ME = me?.data?.me;
  const router = Rotuer;

  const getBackToHome = () => {
    router.push("/");
  };
  return (
    <>
      {!ME ? (
        ""
      ) : (
        <>
          <Box />
          <Box>
            <Box>
              <Box>{data?.post?.id}</Box>
              <Box>{data?.post?.title}</Box>
              <Box>
                <ReactMarkdown
                  escapeHtml={false}
                  source={data?.post?.description}
                ></ReactMarkdown>
              </Box>
            </Box>
          </Box>
        </>
      )}
    </>
  );
};

export default withApollo({ ssr: true })(PostPage);
