import "../styles/globals.css"
import {
  ThemeProvider,
  ColorModeProvider,
  useColorMode,
  Button,
  DarkMode,
} from "@chakra-ui/core"
import customTheme from "../utils/theme"
import React from "react"

function MyApp({ Component, pageProps }) {
  return (
    <ThemeProvider theme={DarkMode}>
      <ColorModeProvider>
        <Component {...pageProps} />
      </ColorModeProvider>
    </ThemeProvider>
  )
}

export default MyApp
