import React from "react"
import styled from "styled-components"
import { useMeQuery, usePostQuery } from "../../generated/graphql"
import NextLink from "next/link"
import ReactMarkdown from "react-markdown"
import Link from "next/link"
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl"
import { withApollo } from "../../utils/withApollo"
import NavBar from "../../components/NavBar"
import SyntaxHighlighter from "react-syntax-highlighter"
import { Box } from "@chakra-ui/core"
import { dark } from "react-syntax-highlighter/dist/cjs/styles/hljs"
import EditDeleteButtons from "../../components/EditDeleteButtons"
interface postPagesProps<> {}

const PostPage: React.FC<postPagesProps> = (props) => {
  const { data, loading } = useGetPostFromUrl()
  const me = useMeQuery()
  const Me = me.data?.me
  console.log(data)
  return (
    <>
      {Me ? (
        <Box
          className="edit-container"
          position="fixed"
          right="15px"
          top="45vh"
        >
          <EditDeleteButtons postId={data?.post?.id} />{" "}
        </Box>
      ) : (
        ""
      )}
      <NavBar />
      <Box display="flex" justifyContent="center" mt="20vh">
        <Box
          minWidth="700px"
          w="55%"
          display="flex"
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
        >
          <Box
            w="80%"
            borderBottom="1px solid black"
            p="50px"
            textAlign="center"
          >
            {data?.post?.title}
          </Box>
          <Box p="20px" mt="5%" textAlign="left" w="90%">
            <ReactMarkdown
              escapeHtml={false}
              source={data?.post?.description}
              renderers={{
                code: Component,
              }}
            ></ReactMarkdown>
          </Box>
        </Box>
      </Box>
    </>
  )
}
const Component = ({ value, language }) => {
  return (
    <SyntaxHighlighter language={language ?? null} style={dark}>
      {value ?? ""}
    </SyntaxHighlighter>
  )
}

export default withApollo({ ssr: true })(PostPage)
