import React from "react"
import styled from "styled-components"
import { useMeQuery, usePostQuery } from "../../generated/graphql"
import NextLink from "next/link"
import ReactMarkdown from "react-markdown"
import Link from "next/link"
import { useGetPostFromUrl } from "../../utils/useGetPostFromUrl"
import { withApollo } from "../../utils/withApollo"
import NavBar from "../../components/NavBar"
import SyntaxHighlighter from "react-syntax-highlighter"
import { Box } from "@chakra-ui/core"
import { dark } from "react-syntax-highlighter/dist/cjs/styles/hljs"
import EditDeleteButtons from "../../components/EditDeleteButtons"
import CustomDocument from "../_document"
interface postPagesProps<> {}

const PostPage: React.FC<postPagesProps> = (props) => {
  const { data, loading } = useGetPostFromUrl()
  const me = useMeQuery()
  const Me = me.data?.me
  console.log(data)
  return (
    <>
      {Me ? (
        <Box position="fixed" right="15px" top="45vh">
          <EditDeleteButtons postId={data?.post?.id} />{" "}
        </Box>
      ) : (
        ""
      )}
      <NavBar />
      <Box display="flex" justifyContent="center" mt="20vh">
        <Box
          className="edit-container"
          minWidth="700px"
          w="55%"
          display="flex"
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
        >
          <Box
            w="80%"
            borderBottom="1px solid #666"
            p="50px"
            textAlign="center"
            fontSize="42px"
          >
            <style jsx global>{`
              @font-face {
                font-family: "Noto Sans";
                src: url("/fonts/noto-sans-v9-latin-regular.woff2");
                font-weight: bold;
                font-style: normal;
                font-display: swap;
              }
            `}</style>
            {data?.post?.title}
          </Box>
          <Box p="20px" mt="5%" textAlign="left" w="80%">
            <ReactMarkdown
              escapeHtml={false}
              source={data?.post?.description}
              renderers={{
                code: Component,
                image: ({
                  alt,
                  src,
                  title,
                }: {
                  alt?: string
                  src?: string
                  title?: string
                }) => (
                  <img
                    alt={alt}
                    src={src}
                    title={title}
                    style={{
                      maxWidth: "700px",
                      margin: "0 auto",
                      display: "table",
                      padding: "40px",
                    }}
                  />
                ),
              }}
            ></ReactMarkdown>
          </Box>
        </Box>
      </Box>
    </>
  )
}

const imageHandle = ({ value }) => {
  return <image></image>
}

const Component = ({ value, language }) => {
  return (
    <SyntaxHighlighter language={language ?? null} style={dark}>
      {value ?? ""}
    </SyntaxHighlighter>
  )
}

export default withApollo({ ssr: true })(PostPage)
